"use strict";window.markdownitFootnoteBulma=function(Request){return function(md){md.sanitize_title=function(title){return title.replace(/ /g,"_")};md.renderer.rules.footnote_ref=function(tokens,idx,options,env,slf){const id=slf.rules.footnote_anchor_name(tokens,idx,options,env,slf);const caption=slf.rules.footnote_caption(tokens,idx,options,env,slf);let refid=id;if(tokens[idx].meta.subId>0){refid+=":"+tokens[idx].meta.subId}return'<sup class="footnote-ref"><a href="#'+Request.buildPath()+footnoteDelimiter+id+'" id="'+Request.buildPath()+footnoteDelimiter+"ref"+refid+'">'+caption+"</a></sup>"};md.renderer.rules.footnote_anchor=function(tokens,idx,options,env,slf){let id=slf.rules.footnote_anchor_name(tokens,idx,options,env,slf);if(tokens[idx].meta.subId>0){id+=":"+tokens[idx].meta.subId}return' <a href="#'+Request.buildPath()+footnoteDelimiter+"ref"+id+'" class="footnote-backref">↩︎</a>'};md.renderer.rules.footnote_open=function(tokens,idx,options,env,slf){let id=slf.rules.footnote_anchor_name(tokens,idx,options,env,slf);if(tokens[idx].meta.subId>0){id+=":"+tokens[idx].meta.subId}return'<li id="'+Request.buildPath()+footnoteDelimiter+id+'" class="footnote-item">'};md.renderer.rules.heading_open=function(tokens,idx,options,env,slf){const title=tokens[idx+1].content;return'<div class="sectionLink"><'+tokens[idx].tag+' id="'+Request.buildPath()+sectionDelimiter+md.sanitize_title(title)+'">'};md.renderer.rules.heading_close=function(tokens,idx,options,env,slf){const title=tokens[idx-1].content;return"</"+tokens[idx].tag+'><a href="#'+Request.buildPath()+sectionDelimiter+md.sanitize_title(title)+'" class="is-hidden">&#182;</a></div>'};md.renderer.rules.text=function(tokens,idx,options,env,slf){return tokens[idx].content}}};